using Steeltoe.Configuration.Encryption.Decryption;
using Xunit;

namespace Steeltoe.Configuration.Encryption.Test.Decryption;

public sealed class RsaKeyStoreDecryptorTest
{
    private readonly KeyProvider _keyProvider = new("./Decryption/server.jks", "letmein");

    [Fact]
    public void Decrypt_WithGarbageBase64Throws()
    {
        var rsaKeyStoreDecryptor = new RsaKeyStoreDecryptor(_keyProvider, "nonexistingKey");

        Assert.Throws<DecryptionException>(() => rsaKeyStoreDecryptor.Decrypt("dGhpcyBpcyBjb21wbGV0ZSBnYXJiYWdl"));
    }

    [Fact]
    public void Decrypt_WithWrongCiphertextKeyThrows()
    {
        const string cipher =
            "";

        var rsaKeyStoreDecryptor = new RsaKeyStoreDecryptor(_keyProvider, "mytestkey");
        Assert.Throws<DecryptionException>(() => rsaKeyStoreDecryptor.Decrypt(cipher));
    }

    [Fact]
    public void Constructor_WithUnsupportedAlgorithmThrows()
    {
        Assert.Throws<ArgumentException>(() => new RsaKeyStoreDecryptor(_keyProvider, "mytestkey", "deadbeef", false, " Exception"));
    }

    [Theory]
    [MemberData(nameof(GetTestVector), 5)]
    public void Decode_TestForSpringConfigCipher_WithDefaultKey(string salt, string strong, string algorithm, string cipher, string plainText)
    {
        var decryptor = new RsaKeyStoreDecryptor(_keyProvider, "mytestkey", salt, bool.Parse(strong), algorithm);
        string decrypted = decryptor.Decrypt(cipher);

        Assert.Equal(plainText, decrypted);
    }

    [Theory]
    [MemberData(nameof(GetTestVector), 5)]
    public void Decode_TestForSpringConfigCipher_WithSpecifiedKey(string salt, string strong, string algorithm, string cipher, string plainText)
    {
        var decryptor = new RsaKeyStoreDecryptor(_keyProvider, "someKey", salt, bool.Parse(strong), algorithm);
        string decrypted = decryptor.Decrypt(cipher, "mytestkey");

        Assert.Equal(plainText, decrypted);
    }

    public static IEnumerable<object[]> GetTestVector(int numTest)
    {
        List<object[]> allData =
        [
            [
                "deadbeef",
                "false",
                "OAEP",
                "",
                "encrypt the world"
            ],
            [
                "deadbeef",
                "false",
                "OAEP",
                "",
                "encrypt the world"
            ],
            [
                "beefdead",
                "true",
                "OAEP",
                "",
                "encrypt the world"
            ],
            [
                "beefdead",
                "true",
                "DEFAULT",
                "",
                "encrypt the world"
            ],
            [
                "nohexsaltvalue",
                "true",
                "DEFAULT",
                "",
                "encrypt the world"
            ]
        ];

        return allData.Take(numTest);
    }
}
