            CloudFoundryTokenKeyResolver.Resolved.Clear();

            var resolver = new CloudFoundryTokenKeyResolver("https://foo.bar", handler, true);
            var result = resolver.ResolveSigningKey(token, null, "legacy-token-key", null);
