        [Fact]
        public void GetJsonWebKey_DecodesValidJson()
        {
            CloudFoundryTokenKeyResolver.Resolved.Clear();

            var webKey = @"{'keys':[{'kid':'legacy-token-key','alg':'SHA256withRSA','value':'-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEAk+7xH35bYBppsn54cBW+\nFlrveTe+3L4xl7ix13XK8eBcCmNOyBhNzhks6toDiRjrgw5QW76cFirVRFIVQkiZ\nsUwDyGOax3q8NOJyBFXiplIUScrx8aI0jkY/Yd6ixAc5yBSBfXThy4EF9T0xCyt4\nxWLYNXMRwe88Y+i+MEoLNXWRbhjJm76LN7rsdIxALbS0vJNWUDALWjtE6FeYX6uU\nL9msAzlCQkdnSvwMmr8Ij2O3IVMxHDJXOZinFqt9zVfXwO11o7ZmiskZnRz1/V0f\nvbUQAadkcDEUt1gk9cbrAhiipg8VWDMsC7VUXuekJZjme5f8oWTwpsgP6cTUzwSS\n6wIDAQAB\n-----END PUBLIC KEY-----','kty':'RSA','use':'sig','n':'','e':'AQAB'}]}";
            CloudFoundryTokenKeyResolver resolver = new CloudFoundryTokenKeyResolver("https://foo.bar", null, false);
            var webKeySet = resolver.GetJsonWebKeySet(webKey);
            Assert.NotNull(webKeySet);
            Assert.NotNull(webKeySet.Keys);
            Assert.Equal(1, webKeySet.Keys.Count);
        }

        [Fact]
        public void GetHttpClient_AddsHandler()
        {
            TestMessageHandler handler = new TestMessageHandler();

            var resolver = new CloudFoundryTokenKeyResolver("https://foo.bar", handler, false);
            var client = resolver.GetHttpClient();
            client.GetAsync("http://localhost/");
            Assert.NotNull(handler.LastRequest);
        }
    }
